```typescript
// import required modules
const { Client, SlashCommandBuilder, GuildMember, EmbedBuilder } = require('discord.js');

// create a new Discord client instance
const client = new Client({ intents: ['Guilds', 'GuildMessages'] });

// (optional) configure bot's token
client.login('YOUR_DISCORD_BOT_TOKEN');

// define slash command for ban
const banCommand = new SlashCommandBuilder()
 .setName('ban') // command name (/ban)
 .setDescription('Bans a user from the guild')
 .addUserOption((option) =>
    option.setName('user').setDescription('User to ban').setRequired(true),
  )
 .addStringOption((option) =>
    option.setName('reason').setDescription('Reason for ban').setOptional(true),
  );

// register command
client.command({
  data: banCommand,
  async execute(interaction) {
    const { member, options } = interaction;
    const userToBan = options.getUser('user');
    const reason = options.getString('reason') || 'No reason provided';

    // check if member has permission to ban
    if (!member.permissions.has('BanMembers')) {
      await interaction.reply({
        embeds: [
          new EmbedBuilder()
           .setColor('RED')
           .setTitle('Error')
           .setDescription(`You don't have permission to ban users.`),
        ],
        ephemeral: true, // reply only to the user who invoked the command
      });
      return;
    }

    // ban user
    try {
      await member.guild.bans.create(userToBan, { reason });
      await interaction.reply({
        embeds: [
          new EmbedBuilder()
           .setColor('GREEN')
           .setTitle('Success')
           .setDescription(`User ${userToBan.user.tag} has been banned for ${reason}.`),
        ],
      });
    } catch (error) {
      await interaction.reply({
        embeds: [
          new EmbedBuilder()
           .setColor('RED')
           .setTitle('Error')
           .setDescription(`Failed to ban ${userToBan.user.tag}. ${error}`,
        ],
        ephemeral: true,
      });
    }
  },
});

// start bot
client.once('ready', () => {
  console.log(`Logged in as ${client.user.tag}!`);
});

client.login('YOUR_DISCORD_BOT_TOKEN');
```

