| sort_runs_merge_aux_cons (x : A) (ys : List A) (acc : List (List A)) :
  have same_type : ∀ (l y), l ∈ acc → l == y.sorted.head ∨ l == y.sorted.head ∥ l.sorted.head = y.sorted.head, from (λ l y, (l.sorted.head = x ∨ y.sorted.head = x) -> l.sorted.head = y.sorted.head),
  have x_less_than_ys : x < y.sorted.head ∨ x == y.sorted.head ∨ x > y.sorted.head.head, from (λ x y, x < y.sorted.head ∨ x == y.sorted.head ∨ x > y.sorted.head.head),
  have x_greater_than_xs : x > xs.head ∨ x == xs.head ∨ x < xs.head, from (λ x x', x > x'.head ∨ x == x'.head ∨ x < x'.head),
  let (left, right) :=
    if x_less_than_ys then ([x], ys)
    else if x_greater_than_xs then (xs, [x] ++ ys)
    else (xs, ys),
  let merged := sort_runs_merge_aux_helper left right acc,
  in (merged.0 ++ acc, merged.1)

-- sort_runs_merge_aux_helper: recursively merge two sorted runs
-- (adapted from Python's implementation)
inductive sort_runs_merge_aux_helper (A : Type) [Ordering α : Ordering A] [DecidableEq α] : (List A) * (List A) -> (List (List A), List A) -> (List (List A), List A)
