    if (!file) {
        cerr << "Error opening file: " << filename << endl;
        return 1;
    }

    // read the file line by line
    string line;
    while (getline(file, line)) {
        // convert the line to lower case for case insensitive search
        for (char& c : line) {
            c = tolower(c);
        }

        // count the occurrences of the word
        size_t pos = line.find(word);
        while (pos != string::npos) {
            count++;
            pos = line.find(word, pos + 1);
        }
    }

    // print the result
    cout << "The word '" << word << "' occurs " << count << " times in the file " << filename << "." << endl;

    return 0;
}
