    mapping (address => uint256) public presaleBalances;

    // Public sale variables
    uint256 public publicSalePrice;

    // Functions
    function mint(address recipient, uint256 tokenAmount) public {
        // Mint tokens for presale
        require(presaleTokenCap >= tokenAmount, "Presale token cap exceeded");
        presaleBalances[recipient] += tokenAmount;
    }

    function buyToken(uint256 tokenAmount) public {
