```javascript
/**
 * Checks if any of the numbers in the input array are prime numbers.
 * @param {number[]} numbers - An array of 5 numbers.
 */
function checkPrimeNumbers(numbers) {
  // Check if input array has exactly 5 elements
  if (numbers.length !== 5) {
    throw new Error('Input array must have exactly 5 elements');
  }

  // Loop through each number in the input array
  numbers.forEach((number) => {
    // Check if the number is a prime number
    if (isPrime(number)) {
      // If it is, print the number to the console
      console.log(`${number} is a prime number`);
    }
  });
}

/**
 * Checks if a number is a prime number.
 * @param {number} number - The number to check.
 * @returns {boolean} True if the number is prime, false otherwise.
 */
function isPrime(number) {
  // Numbers less than 2 are not prime
  if (number < 2) {
    return false;
  }

  // Loop through all numbers up to the square root of the input number
  for (let i = 2, sqrt = Math.sqrt(number); i <= sqrt; i++) {
    // If the number is divisible by any of these numbers, it's not prime
    if (number % i === 0) {
      return false;
    }
  }

  // If we reach this point, the number is prime
  return true;
}

// Example usage:
const numbers = [1, 2, 3, 4, 5];
checkPrimeNumbers(numbers);
```
