\usepackage{tikz}
\usetikzlibrary{positioning,calc}
\usepackage{pgffor}

% Define colors
\definecolor{black}{HTML}{000000}
\definecolor{white}{HTML}{EAEAEA}
\definecolor{red}{HTML}{C54B39}
\definecolor{blue}{HTML}{276D8E}
\definecolor{green}{HTML}{5CB85C}
\definecolor{brown}{HTML}{A04653}

% Define squares
