    \foreach \n in {1,...,\the\numexpr\pagetotal-\pgflinewidth-(\dimexpr\paperwidth/\numberofrows pt)} {
        \ifoddpt{\pgfmathresult}{\fill[black]; } {\fill[white]; }}
        \advance\row by -1;
    }
}

% Draw the board
\newcommand*\drawchessboard[1]{%
    \begin{tikzpicture}[remember picture, overlay]
        \coordinate (topleft) at ($(current page text area.west |- current page text area.north)+(-1cm,-1cm)$);
        \coordinate (bottomright) at ($(current page text area.east|-current page text area.south)+(1cm,1cm)$);

        % Grid lines
        \draw[thin,gray!30] (topleft) -- coordinate (a1) (bottomright);
        \foreach \i in {1,...,8} {
            \draw[thin,gray!30] (\pgfpointadd{(a1)}{2.5cm*(\i-1)})--++(1.25cm,0);
            \draw[thin,gray!30] (\pgfpointadd{(a1)}{2.5cm*(mod(\i+7,8)-1)})--++(1.25cm,0);
        }

        % Board pattern
        \path[pattern=mycheckerboard, pattern color={#{\PGFkeysValueOf{pieceColor}\relax}}] (topleft)|-(bottomright) node[anchor=center]{\large\textbf{Rank}};
        \foreach \r in {1,...,8} {
            \edef\temp{\noexpand\draw[line width=0.4pt]}
            \expandafter\temp
            (\pgfpointadd{(a1)}{2.5cm*(\r-1)})--++(1.25cm,0);
            \edef\temp{\noexpand\draw[line width=0.4pt]}
            \expandafter\temp
            (\pgfpointadd{(a1)}{2.5cm*(mod(\r+7,8)-1)})--++(1.25cm,0);
            \foreach \c in {a,b,c,d,e,f,g,h} {
                \edef\temp{\noexpand\filldraw[\PGFkeysValueOf{squareColor}] (\pgfpointadd{(a1)}{2.5cm*(\r-1)+0.6cm*(\alphabeticindexof{\c}-\alphabetindexof{a})}|\-(bottomright)) circle (0.25em);}
                \expandaafter\temp
            }
        }

        % Pieces
        \foreach \pos/\piece in {{a1,R},{b1,N},{c1,B},{d1,Q},{e1,K},{f1,B},{g1,N},{h1,R}}{
            \edef\temp{\noexpand\node[anchor=\PGFkeysValueOf{pieceAlignment},scale=0.8] at (\pos){%
                    \ifx\piece R \else \textcolor{white}{\fi \PGFkeysValueOf{pieceSymbol}}\ PGFForeachListWithoutValue{\piecelist}{%
                        \ifx\piecelist\empty{} {}
                        \else \ifthenelse{\equal{\piece}{\piecelist}}{\fill[black];}{\pgftransformshift{-0.2em};\fill[white];}
                        \global\let\piecelist\empty
                        \repeat}
                    };}
            \expandafter\temp
        }

        % FEN decoding
        \PGFkeysValueOf{fen}|={\PGFdecorateBoardWithFen}
        \PGFdecorateBoardWithFen
    \end{tikzpicture}%
}

% Custom command to set up the board
\newcommand{\setupchessboard}[2][pieceColor=black, squareColor=white, pieceAlignment=base, piecelist={PP PP PP PP PP PP PP PP}, pieceSymbol=.\space\textbullet\space, fen=rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR] {
