    \PGFkey{/pgfsysdriver}{custom.sty}
    \PGFkeys{
        /chessboard/.is family, /, local, prefix=,
        pieceColor=#1,
        squareColor=#2,
        pieceAlignment=#3,
        piecelist=#2,
        pieceSymbol=#4,
        fen=#5
