Here's the complete code:

```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <sstream>

int main() {
    std::string filename;
    std::string searchWord;
    std::string line;
    int count = 0;

    // Get the filename and the word to search from the user
    std::cout << "Enter the filename: ";
    std::getline(std::cin, filename);
    std::cout << "Enter the word to search: ";
    std::getline(std::cin, searchWord);

    // Open the file
    std::ifstream file(filename);

    // Check if the file was opened successfully
    if (!file.is_open()) {
        std::cerr << "Could not open the file - '" << filename << "'" << std::endl;
        return 1;
    }

    // Read lines from the file
    while (getline(file, line)) {
        std::istringstream iss(line);
        std::string word;

        // Read each word from the line
        while (iss >> word) {
            // Increment count if the current word matches the search word
            if (word == searchWord) {
                count++;
            }
        }
    }

    // Close the file
    file.close();

    // Output the result
