```c
#include <SDL.h>
#include <stdio.h>
#include <stdlib.h>

#define WINDOW_WIDTH 800
#define WINDOW_HEIGHT 600
#define SNAKE_SIZE 20

typedef struct {
    int x, y;
} Point;

typedef enum {
    DIR_UP,
    DIR_DOWN,
    DIR_LEFT,
    DIR_RIGHT
} Direction;

typedef struct {
    Point body[100];
    int length;
    Direction direction;
} Snake;

Snake snake = {
    .body = {{WINDOW_WIDTH / 2, WINDOW_HEIGHT / 2}},
    .length = 1,
    .direction = DIR_RIGHT
};

Point food = {100, 100};

void init() {
    if (SDL_Init(SDL_INIT_VIDEO) < 0) {
        printf("SDL could not initialize! SDL_Error: %s\n", SDL_GetError());
        exit(1);
    }
}

void cleanup() {
    SDL_DestroyWindow(window);
    SDL_Quit();
}

void handle_input() {
    SDL_Event e;
    while (SDL_PollEvent(&e) != 0) {
        if (e.type == SDL_QUIT) {
            running = 0;
        } else if (e.type == SDL_KEYDOWN) {
            switch (e.key.keysym.sym) {
                case SDLK_UP:
                    snake.direction = DIR_UP;
                    break;
                case SDLK_DOWN:
                    snake.direction = DIR_DOWN;
                    break;
                case SDLK_LEFT:
                    snake.direction = DIR_LEFT;
                    break;
                case SDLK_RIGHT:
                    snake.direction = DIR_RIGHT;
                    break;
            }
        }
    }
}

void update() {
    for (int i = snake.length - 1; i > 0; i--) {
        snake.body[i] = snake.body[i - 1];
    }

    switch (snake.direction) {
        case DIR_UP:
            snake.body[0].y -= SNAKE_SIZE;
            break;
        case DIR_DOWN:
            snake.body[0].y += SNAKE_SIZE;
            break;
        case DIR_LEFT:
            snake.body[0].x -= SNAKE_SIZE;
            break;
        case DIR_RIGHT:
            snake.body[0].x += SNAKE_SIZE;
            break;
    }

    if (snake.body[0].x < 0 || snake.body[0].x >= WINDOW_WIDTH ||
        snake.body[0].y < 0 || snake.body[0].y >= WINDOW_HEIGHT) {
        running = 0;
    }

    for (int i = 1; i < snake.length; i++) {
        if (snake.body[0].x == snake.body[i].x &&
            snake.body[0].y == snake.body[i].y) {
            running = 0;
        }
    }

    if (snake.body[0].x == food.x && snake.body[0].y == food.y) {
        snake.length++;
        food.x = rand() % (WINDOW_WIDTH / SNAKE_SIZE) * SNAKE_SIZE;
        food.y = rand() % (WINDOW_HEIGHT / SNAKE_SIZE) * SNAKE_SIZE;
    }
}

void render() {
    SDL_SetRenderDrawColor(renderer, 0, 0, 0, 255);
    SDL_RenderClear(renderer);

    SDL_SetRenderDrawColor(renderer, 255, 255, 255, 255);
    for (int i = 0; i < snake.length; i++) {
        SDL_Rect rect = {snake.body[i].x, snake.body[i].y, SNAKE_SIZE, SNAKE_SIZE};
        SDL_RenderFillRect(renderer, &rect);
    }

    SDL_Rect food_rect = {food.x, food.y, SNAKE_SIZE, SNAKE_SIZE};
    SDL_SetRenderDrawColor(renderer, 255, 0, 0, 255);
    SDL_RenderFillRect(renderer, &food_rect);

    SDL_RenderPresent(renderer);
}

int main(int argc, char* args[]) {
    init();

    SDL_Window* window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, WINDOW_WIDTH, WINDOW_HEIGHT, SDL_WINDOW_SHOWN);
    SDL_Renderer* renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);

    int running = 1;

    while (running) {
        handle_input();
        update();
        render();
    }

    cleanup();

    return 0;
}
```

This example uses SDL2 to create a simple snake game without animations. To add animations, you can use SDL_image to load PNG images for the snake's body and food, and then blit them onto the screen instead of using filled rectangles. You'll also need to implement a timer to change the displayed image at regular intervals.

