function isPrime(num) {
  // Check if the number is less than or equal to 1
  if (num <= 1) {
    return false;
  }

  // Check if the number is divisible by any other number between 2 and the square root of the given number
  for (let i = 2; i < Math.sqrt(num); i++) {
    if (num % i === 0) {
      return false;
    }
  }

  // If no divisors were found, the number is prime
  return true;
}

