```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <algorithm>

using namespace std;

// Function to convert a string to lowercase
string toLowerCase(const string& str) {
    string lowerStr = str;
    transform(lowerStr.begin(), lowerStr.end(), lowerStr.begin(), ::tolower);
    return lowerStr;
}

// Function to count occurrences of a word in a line
int countWordInLine(const string& line, const string& word) {
    int count = 0;
    size_t pos = line.find(word);
    while (pos != string::npos) {
        count++;
        pos = line.find(word, pos + word.length());
    }
    return count;
}

int main() {
    string filename;
    string word;
    cout << "Enter the filename: ";
    cin >> filename;
    cout << "Enter the word to count: ";
    cin >> word;

    // Convert the word to lowercase for case-insensitive comparison
    string lowerWord = toLowerCase(word);

    ifstream file(filename);
    if (!file.is_open()) {
        cerr << "Could not open the file " << filename << endl;
        return 1;
    }

    string line;
    int totalOccurrences = 0;

    while (getline(file, line)) {
        // Convert the line to lowercase for case-insensitive comparison
        string lowerLine = toLowerCase(line);
        totalOccurrences += countWordInLine(lowerLine, lowerWord);
    }

    file.close();

    cout << "The word \"" << word << "\" occurred " << totalOccurrences << " times in the file." << endl;

    return 0;
}
