const { Client, Collection, Permissions, SlashCommandBuilder } = require('discord.js');
const { token } = require('./config.json');

const client = new Client({ intents: ['Guilds', 'GuildMembers', 'GuildBans'] });

client.commands = new Collection();

const banCommand = new SlashCommandBuilder()
  .setName('ban')
  .setDescription('Bans a mentioned user and provides a reason for the ban.')
  .addUserOption(option =>
    option.setName('user')
      .setDescription('The user to be banned')
      .setRequired(true)
  )
  .addStringOption(option =>
    option.setName('reason')
      .setDescription('The reason for the ban')
      .setRequired(true)
  );

client.commands.set('ban', banCommand);

client.on('interactionCreate', async interaction => {
  if (!interaction.isChatInputCommand()) return;

  const command = client.commands.get(interaction.commandName);

  if (!command) {
    console.error(`No command found for interaction: ${interaction.commandName}`);
    return;
  }

  try {
    await interaction.deferReply();

    if (command.options.gets.user) {
      const user = interaction.options.getUser('user');
      const reason = interaction.options.getString('reason');

      if (!interaction.guild.me.permissions.has(Permissions.FLAGS.BAN_MEMBERS)) {
        return interaction.editReply('I lack the necessary permissions to ban members.');
      }

      if (interaction.user.id === user.id) {
        return interaction.editReply('You cannot ban yourself.');
      }

      await interaction.guild.members.ban(user, { reason });

      return interaction.editReply(`Successfully banned ${user} for: ${reason}`);
    }

    throw new Error('Invalid command usage.');
  } catch (error) {
    console.error(`Command failed: ${interaction.commandName}`, error);
    await interaction.editReply('An error occurred while executing the command.');
  }
});

client.login(token);
```

