SDL_Window* initSDL() {
    if (SDL_Init(SDL_INIT_VIDEO) < 0) {
        fprintf(stderr, "SDL could not initialize! SDL_Error: %s\n", SDL_GetError());
        return NULL;
    }

    SDL_Window* window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_UNDEFINED, SDL_WINDOWPOS_UNDEFINED, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN);
    if (window == NULL) {
        fprintf(stderr, "Window could not be created! SDL_Error: %s\n", SDL_GetError());
        SDL_Quit();
        return NULL;
    }

    return window;
}

// Loads an image into an SDL_Texture
SDL_Texture* loadTexture(const char* file, SDL_Renderer* renderer) {
    SDL_Surface* loadedImage = SDL_LoadBMP(file);
    if (loadedImage == NULL) {
        fprintf(stderr, "Unable to load image %s! SDL_Error: %s\n", file, SDL_GetError());
        return NULL;
    }

    SDL_Texture* texture = SDL_CreateTextureFromSurface(renderer, loadedImage);
    SDL_FreeSurface(loadedImage);
    if (texture == NULL) {
        fprintf(stderr, "Unable to create texture from %s! SDL_Error: %s\n", file, SDL_GetError());
        return NULL;
    }

    return texture;
}

int main(int argc, char* args[]) {
    SDL_Window* window = initSDL();
    if (window == NULL) {
        return 1;
    }

    SDL_Renderer* renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);
    if (renderer == NULL) {
        fprintf(stderr, "Renderer could not be created! SDL_Error: %s\n", SDL_GetError());
