    SDL_Texture* backgroundTexture = loadTexture("background.bmp", renderer);
    SDL_Texture* snakeTexture = loadTexture("snake.bmp", renderer);
    SDL_Texture* foodTexture = loadTexture("food.bmp", renderer);

    if (backgroundTexture == NULL || snakeTexture == NULL || foodTexture == NULL) {
        SDL_DestroyRenderer(renderer);
        SDL_DestroyWindow(window);
        SDL_Quit();
        return 1;
    }

    SDL_Rect snakePos = {0, 0, SNAKE_SIZE, SNAKE_SIZE};
    SDL_Rect foodPos = {SCREEN_WIDTH / 2, SCREEN_HEIGHT / 2, FOOD_SIZE, FOOD_SIZE};

    bool running = true;
    while (running) {
        SDL_Event e;
        while (SDL_PollEvent(&e)) {
            switch (e.type) {
                case SDL_QUIT:
                    running = false;
                    break;
                case SDL_KEYDOWN:
                    switch (e.key.keysym.sym) {
                        case SDLK_UP:
                            // Move snake up
                            break;
                        case SDLK_DOWN:
                            // Move snake down
                            break;
                        case SDLK_LEFT:
                            // Move snake left
                            break;
                        case SDLK_RIGHT:
                            // Move snake right
                            break;
                    }
                    break;
            }
        }

        // Update game state here (move snake, check for collisions, etc.)

        // Render
        SDL_RenderClear(renderer);
        SDL_RenderCopy(renderer, backgroundTexture, NULL, NULL);
        SDL_RenderCopy(renderer, snakeTexture, NULL, &snakePos);
        SDL_RenderCopy(renderer, foodTexture, NULL, &foodPos);
        SDL_RenderPresent(renderer);

        SDL_Delay(1000 / 60); // 60 FPS
    }

    // Clean up
    SDL_DestroyTexture(backgroundTexture);
    SDL_DestroyTexture(snakeTexture);
    SDL_DestroyTexture(foodTexture);
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();

    return 0;
}
