function isPrime(num) {
    // Check if number is less than 2 (not prime)
    if (num < 2) return false;
    // Check if number is 2 (prime)
    if (num === 2) return true;
    // Check if number is even (not prime)
    if (num % 2 === 0) return false;
    // Check odd numbers from 3 up to the square root of num
    for (let i = 3; i <= Math.sqrt(num); i += 2) {
        if (num % i === 0) return false;
    }
    return true;
}

function checkForPrimes(numbers) {
    if (numbers.length !== 5) {
        console.log("Error: Please provide an array of 5 numbers.");
        return;
    }
    numbers.forEach(num => {
        if (isPrime(num)) {
            console.log(num);
        }
    });
}

// Example usage:
checkForPrimes([7, 12, 19, 21, 31]); // This should print: 7, 19, 31
```

