```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <cctype> // for std::tolower

int main() {
    std::ifstream file("example.txt"); // Open the file for reading
    std::string line; // To store each line of the file
    std::string word_to_find = "example"; // The word we're looking for
    int word_count = 0; // Counter for the word occurrences

    if (!file.is_open()) {
        std::cerr << "Unable to open file" << std::endl;
        return 1;
    }

    while (std::getline(file, line)) { // Read each line from the file
        // Convert the line to lowercase for case-insensitive search
        std::transform(line.begin(), line.end(), line.begin(),
                       [](unsigned char c) { return std::tolower(c); });

        size_t pos = 0;
        while ((pos = line.find(word_to_find, pos)) != std::string::npos) {
            // Check if the word is surrounded by non-alphanumeric characters
            // to avoid counting words that are part of other words
            bool is_word_boundary =
                (pos == 0 || !std::isalnum(line[pos - 1])) &&
                (pos + word_to_find.size() == line.size() || !std::isalnum(line[pos + word_to_find.size()]));

            if (is_word_boundary) {
                word_count++;
            }

            pos += word_to_find.size(); // Move to the next position after the found word
        }
    }

    file.close(); // Close the file

    std::cout << "The word '" << word_to_find << "' appears " << word_count << " times." << std::endl;

    return 0;
}
